mapper.insert:
insert into bl_blog(blog_id, blog_title, blog_image, blog_content, blog_type, blog_remark, blog_source,
                    created_time)
values (#{blogId}, #{blogTitle}, #{blogImage}, #{blogContent}, #{blogType},
        #{blogRemark}, #{blogSource},#{createdTime}
       )

mapper.update:
update bl_blog
set deleted = 1
where blog_id = #{value}

update bl_blog
set version = version + 1,
    update_time = #{updateTime}

    ,blog_Title = #{blogTitle}


    ,blog_Image = #{blogImage}


    ,blog_Content = #{blogContent}


    ,blog_Goods = #{blogGoods}


    ,blog_Read = #{blogRead}


    ,blog_Collection = #{blogCollection}


    ,blog_Type = #{blogType}


    ,blog_Remark = #{blogRemark}


    ,blog_Comment = #{blogComment}


    ,blog_source = #{blogSource}

where blog_id = #{blogId}
and version = #{version}
and deleted = 0

update bl_blog
set blog_goods = blog_goods + 1
where blog_id = #{value}

update bl_blog
set blog_comment = blog_comment + 1
where blog_id = #{value}

update bl_blog
set blog_collection = blog_collection + 1
where blog_id = #{value}

update bl_blog
set blog_comment = blog_comment - 1
where blog_id = #{value}

update bl_blog
set blog_collection = blog_collection - 1
where blog_id = #{value}

mapper.select:
select blog_id,blog_title, blog_image, blog_goods, blog_read, blog_collection, blog_type, blog_remark, blog_comment,blog_source,created_time, update_time,version from bl_blog where deleted = 0
and blog_title like CONCAT('%', #{params.blogTitle}, '%')
and blog_type = #{typeId}

mapper.update:
update bl_blog
set blog_read = blog_read + 1
where blog_id = #{blogId}
